"use client"

import { zodResolver } from "@hookform/resolvers/zod"
import { useState } from "react"
import { useForm } from "react-hook-form"
import { toast } from "sonner"

import { Button } from "@/components/ui/button"
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form"
import { Input } from "@/components/ui/input"

import { sanitizeData } from "@/utils/sanitize"

import { LoadingSpinner } from "@/registry/components/layout/loading/loading-spinner"
import { AlertError } from "@/registry/components/shared/alert/alert-error"
import { AlertSuccess } from "@/registry/components/shared/alert/alert-success"
import {
  type ForgotPasswordFormValues,
  forgotPasswordSchema,
} from "@/validations/auth/forgot-password.schema"

/**
 * Estado do formul√°rio de recupera√ß√£o de senha
 *
 * Centraliza o controle de UI e feedback do formul√°rio:
 * - Loading durante requisi√ß√µes
 * - Mensagens de erro/sucesso
 */
type FormState = {
  isLoading: boolean
  errorMessage: string | null
  successMessage: string | null
}

/**
 * Formul√°rio de recupera√ß√£o de senha
 *
 * Implementa o fluxo completo de recupera√ß√£o com:
 * - Valida√ß√£o de campos
 * - Feedback visual
 * - Tratamento de erros
 * - Logs para auditoria
 */
export function ForgotPasswordForm() {
  /**
   * Estado inicial do formul√°rio
   *
   * Controla o estado de loading e mensagens
   * de feedback durante o processo.
   */
  const [formState, setFormState] = useState<FormState>({
    isLoading: false,
    errorMessage: null,
    successMessage: null,
  })

  /**
   * Configura√ß√£o do formul√°rio
   *
   * Define valores iniciais e regras de valida√ß√£o
   * usando o schema Zod para type-safety.
   */
  const form = useForm<ForgotPasswordFormValues>({
    resolver: zodResolver(forgotPasswordSchema),
    defaultValues: {
      email: "",
    },
  })

  /**
   * Atualiza o estado do formul√°rio
   *
   * Preserva o estado anterior e aplica apenas as
   * mudan√ßas necess√°rias de forma segura.
   */
  const updateFormState = (updates: Partial<FormState>) => {
    setFormState((prev) => ({ ...prev, ...updates }))
  }

  /**
   * Processa o envio do formul√°rio
   *
   * Gerencia o fluxo completo de recupera√ß√£o:
   * - Valida√ß√£o dos dados
   * - Sanitiza√ß√£o
   * - Envio √† API
   * - Tratamento da resposta
   * - Feedback ao usu√°rio
   * - Logs para auditoria
   */
  async function onSubmit(data: ForgotPasswordFormValues) {
    try {
      // Inicia o processo e limpa feedback anterior
      updateFormState({
        isLoading: true,
        errorMessage: null,
        successMessage: null,
      })

      // Sanitiza dados para prevenir inje√ß√£o
      const sanitizedData = sanitizeData(data, forgotPasswordSchema)

      // TODO: Envia dados para recupera√ß√£o
      // biome-ignore lint/style/noMagicNumbers: Magic number detected. Extract it to a constant with a meaningful name.
      await new Promise((resolve) => setTimeout(resolve, 1000))

      // Processa recupera√ß√£o bem-sucedida
      const successMsg = `Email de recupera√ß√£o enviado para ${sanitizedData.email}.`
      updateFormState({ successMessage: successMsg })

      // Exibe feedback visual
      toast.success("Email Enviado", {
        description: "Verifique sua caixa de entrada para redefinir sua senha",
        classNames: {
          success: "!bg-green-200 !text-green-600 dark:!bg-green-950",
          description: "!text-muted-foreground",
        },
      })

      // Limpa o formul√°rio ap√≥s sucesso
      form.reset()
    } catch (error) {
      // Trata erros inesperados
      const errorMsg =
        error instanceof Error
          ? error.message
          : "Erro ao processar recupera√ß√£o de senha"
      updateFormState({ errorMessage: errorMsg })
    } finally {
      // Finaliza o processo independente do resultado
      updateFormState({ isLoading: false })
    }
  }

  return (
    <Form {...form}>
      <form
        className="space-y-6"
        onSubmit={(e) => {
          form.handleSubmit(onSubmit)(e)
        }}
      >
        {/* Campo de email - valida√ß√£o e sanitiza√ß√£o */}
        <FormField
          control={form.control}
          name="email"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Email</FormLabel>
              <FormControl>
                <Input
                  {...field}
                  disabled={formState.isLoading}
                  placeholder="seu@email.com"
                  type="email"
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Feedback visual - mensagens de erro/sucesso */}
        {formState.errorMessage && (
          <AlertError title={formState.errorMessage} />
        )}
        {formState.successMessage && (
          <AlertSuccess title={formState.successMessage} />
        )}

        {/* Bot√£o de a√ß√£o - controle do fluxo de recupera√ß√£o */}
        <Button
          className="w-full cursor-pointer"
          disabled={formState.isLoading}
          type="submit"
        >
          {formState.isLoading ? (
            <LoadingSpinner className="size-4" />
          ) : (
            "Enviar email de recupera√ß√£o"
          )}
        </Button>
      </form>
    </Form>
  )
}

