            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-foreground">
                    Nome:<span className="-ml-2 text-red-600">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
