              type="file"
            />

            <Button
              className="flex cursor-pointer items-center space-x-2"
              disabled={formState.isLoading}
              onClick={() => {
                fileInputRef.current?.click()
              }}
              size="sm"
              type="button"
              variant="outline"
            >
              {formState.isLoading ? (
                <LoadingSpinner className="h-4 w-4" />
              ) : (
                <Upload className="h-4 w-4" />
