        },
      })

      // Redireciona para a p√°gina inicial
      router.push("/")
    } catch (error) {
      // Trata erros inesperados
      const errorMsg =
        error instanceof Error ? error.message : "Erro ao excluir conta"
      updateFormState({ errorMessage: errorMsg })
    } finally {
      // Finaliza o processo independente do resultado
      updateFormState({ isLoading: false })
    }
  }

  return (
    <Card className="mt-16 border-red-800 bg-red-900">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2 text-lg text-red-500">
          <AlertTriangle className="h-5 w-5" />
          <span>Zona de Perigo</span>
        </CardTitle>
        <CardDescription className="text-red-200">
          Depois de excluir sua conta, n√£o h√° como voltar atr√°s. Por favor,
          tenha certeza.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <AlertDialog
          onOpenChange={handleDialogChange}
          open={formState.showDeleteDialog}
        >
          <AlertDialogTrigger asChild>
            <Button
              className="flex w-full cursor-pointer items-center space-x-2 sm:w-auto"
              disabled={formState.isLoading}
              type="button"
              variant="destructive"
            >
              {formState.isLoading ? (
                <LoadingSpinner className="h-4 w-4" />
              ) : (
                <Trash2Icon className="h-4 w-4" />
              )}
              <span>Excluir conta</span>
            </Button>
          </AlertDialogTrigger>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle className="flex items-center space-x-2 text-red-500">
                <AlertTriangle className="h-5 w-5" />
                <span>Excluir Conta</span>
              </AlertDialogTitle>
              <AlertDialogDescription className="text-muted-foreground">
                Esta a√ß√£o n√£o pode ser desfeita. Isso excluir√° permanentemente
                sua conta e remover√° todos os seus dados de nossos servidores.
                <br />
                <br />
                <strong className="text-red-500">
                  Voc√™ tem certeza absoluta que deseja excluir sua conta?
                </strong>
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel className="cursor-pointer">
                Cancelar
              </AlertDialogCancel>
              <AlertDialogAction
                className="cursor-pointer bg-red-600 text-white hover:bg-red-700"
                disabled={formState.isLoading}
                onClick={() => {
                  handleDeleteAccount()
                }}
              >
                {formState.isLoading ? (
                  <>
                    <LoadingSpinner className="mr-2 h-4 w-4" />
                    Excluindo...
                  </>
                ) : (
                  "Sim, excluir minha conta"
                )}
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </CardContent>
    </Card>
  )
}

