import {
  type Cell,
  type ColumnDef,
  type ColumnFiltersState,
  flexRender,
  getCoreRowModel,
  getFacetedRowModel,
  getFacetedUniqueValues,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  type HeaderGroup,
  type OnChangeFn,
  type Row,
  type SortingState,
  useReactTable,
  type VisibilityState,
} from "@tanstack/react-table"
import { useState } from "react"

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"

import { DataTablePagination } from "@/registry/components/shared/data-table/inc/data-table-pagination"
import { DataTableToolbar } from "@/registry/components/shared/data-table/inc/data-table-toolbar"
import { LoadingSpinner } from "@/registry/components/shared/loading/loading-spinner"

type DataTableProps<TData, TValue> = {
  columns: ColumnDef<TData, TValue>[]
  data: TData[]
  page: number
  perPage: number
  pageCount: number
  setPage: (page: number) => void
  setPerPage: (perPage: number) => void
  setSort: (sort: SortingState) => void
  setName: (name: string) => void
  name: string
  sort: SortingState
  isLoading: boolean
  columnVisibility: VisibilityState
  setColumnVisibility: OnChangeFn<VisibilityState>
  filterParams: string
  setFilterParams: (filterParams: string) => void
}

export function DataTable<TData, TValue>({
  columns,
  data,
  page,
  perPage,
  pageCount,
  setPage,
  setPerPage,
  setSort,
  setName,
  name,
  sort,
  isLoading,
  columnVisibility,
  setColumnVisibility,
  filterParams,
  setFilterParams,
}: DataTableProps<TData, TValue>) {
  const [rowSelection, setRowSelection] = useState({})
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([])

  const table = useReactTable({
    data,
    columns,
    state: {
      sorting: sort,
      columnVisibility,
      rowSelection,
      columnFilters,
      pagination: {
        pageIndex: page - 1,
        pageSize: perPage,
      },
    },
    enableRowSelection: true,
    onRowSelectionChange: setRowSelection,
    onSortingChange: (updater) => {
      if (typeof updater === "function") {
        setSort(updater(table.getState().sorting))
      } else {
        setSort(updater)
      }
    },
    onColumnFiltersChange: setColumnFilters,
    onColumnVisibilityChange: setColumnVisibility,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
    manualPagination: true,
    manualSorting: true,
    pageCount,
    onPaginationChange: (updater) => {
      if (typeof updater === "function") {
        const newPagination = updater(table.getState().pagination)
        setPage(newPagination.pageIndex + 1)
        setPerPage(newPagination.pageSize)
      } else {
        const newPagination = updater
        setPage(newPagination.pageIndex + 1)
        setPerPage(newPagination.pageSize)
      }
    },
  })

  return (
    <div className="flex h-[38rem] w-full flex-col gap-4">
      <DataTableToolbar
        filterParams={filterParams}
        name={name}
        setColumnVisibilityAction={setColumnVisibility}
        setFilterParamsAction={setFilterParams}
        setNameAction={setName}
        table={table}
      />
      <div className="flex flex-2/3 flex-col overflow-hidden rounded-md border">
        <Table>
          <TableHeader className="sticky top-0 z-10 outline-1 outline-border">
            {table.getHeaderGroups().map((headerGroup: HeaderGroup<TData>) => (
              <TableRow
                className="bg-card px-4 hover:bg-card"
                key={headerGroup.id}
              >
                {headerGroup.headers.map(
                  (header: HeaderGroup<TData>["headers"][number]) => {
                    return (
                      <TableHead
                        className="px-4"
                        colSpan={header.colSpan}
                        key={header.id}
                      >
                        {header.isPlaceholder
                          ? null
                          : flexRender(
                              header.column.columnDef.header,
                              header.getContext()
                            )}
                      </TableHead>
                    )
                  }
                )}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody className="text-muted-foreground">
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row: Row<TData>) => (
                <TableRow
                  className="hover:text-foreground"
                  data-state={row.getIsSelected() && "selected"}
                  key={row.id}
                >
                  {row.getVisibleCells().map((cell: Cell<TData, unknown>) => (
                    <TableCell
                      className="min-w-fit max-w-48 truncate px-4 py-1.5"
                      key={cell.id}
                    >
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext()
                      )}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  className="h-24 text-center hover:bg-card"
                  colSpan={columns.length}
                >
                  {isLoading ? <LoadingSpinner /> : "No results."}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>
      <DataTablePagination
        setPage={setPage}
        setPerPage={setPerPage}
        table={table}
      />
    </div>
  )
}

import type { Column } from "@tanstack/react-table"
import { CheckIcon, CirclePlusIcon } from "lucide-react"

import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from "@/components/ui/command"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { Separator } from "@/components/ui/separator"

import { cn } from "@/lib/utils"

type DataTableFacetedFilterProps<TData, TValue> = {
  column?: Column<TData, TValue>
  title?: string
  options: {
    label: string
    value: string
    icon?: React.ComponentType<{ className?: string }>
  }[]
  filterParams: string
  setFilterParams: (filterParams: string) => void
}

export function DataTableFacetedFilter<TData, TValue>({
  column,
  title,
  options,
  filterParams,
  setFilterParams,
}: DataTableFacetedFilterProps<TData, TValue>) {
  const facets = column?.getFacetedUniqueValues()
  const selectedValues = new Set(filterParams.split(",").filter(Boolean))

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          className="h-8 cursor-pointer border-dashed"
          size="sm"
          variant="outline"
        >
          <CirclePlusIcon className="mr-2 h-4 w-4" />
          {title}
          {selectedValues?.size > 0 && (
            <>
              <Separator className="mx-2 h-4" orientation="vertical" />
              <Badge
                className="rounded-sm px-1 font-normal lg:hidden"
                variant="secondary"
              >
                {selectedValues.size}
              </Badge>
              <div className="hidden space-x-1 lg:flex">
                {selectedValues.size > 2 ? (
                  <Badge
                    className="rounded-sm px-1 font-normal"
                    variant="secondary"
                  >
                    {selectedValues.size} selected
                  </Badge>
                ) : (
                  options
                    .filter((option) => selectedValues.has(option.value))
                    .map((option) => (
                      <Badge
                        className="rounded-sm px-1 font-normal"
                        key={option.value}
                        variant="secondary"
                      >
                        {option.label}
                      </Badge>
                    ))
                )}
              </div>
            </>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="start" className="w-[200px]">
        <Command>
          <CommandInput placeholder={title} />
          <CommandList>
            <CommandEmpty>No results found.</CommandEmpty>
            <CommandGroup>
              {options.map((option) => {
                const isSelected = selectedValues.has(option.value)
                return (
                  <CommandItem
                    key={option.value}
                    onSelect={() => {
                      if (isSelected) {
                        selectedValues.delete(option.value)
                      } else {
                        selectedValues.add(option.value)
                      }
                      const newFilterParams =
                        Array.from(selectedValues).join(",")
                      setFilterParams(newFilterParams)
                      column?.setFilterValue(
                        newFilterParams.length
                          ? newFilterParams.split(",")
                          : undefined
                      )
                    }}
                  >
                    <div
                      className={cn(
                        "mr-2 flex h-4 w-4 items-center justify-center rounded-sm border border-primary",
                        isSelected
                          ? "bg-primary text-primary-foreground"
                          : "opacity-50 [&_svg]:invisible"
                      )}
                    >
                      <CheckIcon className="h-4 w-4" />
                    </div>
                    {option.icon && (
                      <option.icon className="mr-2 h-4 w-4 text-muted-foreground" />
                    )}
                    <span>{option.label}</span>
                    {facets?.get(option.value) && (
                      <span className="ml-auto flex h-4 w-4 items-center justify-center font-mono text-xs">
                        {facets.get(option.value)}
                      </span>
                    )}
                  </CommandItem>
                )
              })}
            </CommandGroup>
            {selectedValues.size > 0 && (
              <>
                <CommandSeparator />
                <CommandGroup>
                  <CommandItem
                    className="justify-center text-center"
                    onSelect={() => {
                      setFilterParams("")
                      column?.setFilterValue(undefined)
                    }}
                  >
                    Clear filters
                  </CommandItem>
                </CommandGroup>
              </>
            )}
          </CommandList>
        </Command>
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

import { EditIcon, EyeIcon, MoreVerticalIcon, TrashIcon } from "lucide-react"
import { useState } from "react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

import { DeleteConfirmationDialog } from "@/registry/components/shared/dialog/delete-confirmation-dialog"

type DataTableRowActionsProps<TItem, TDetailsDialogProps, TFormDialogProps> = {
  item: TItem
  onDelete: (id: string) => Promise<void>
  isLoadingDelete: boolean
  DetailsDialogComponent?: React.ComponentType<TDetailsDialogProps>
  FormDialogComponent?: React.ComponentType<TFormDialogProps>
  detailsDialogProps?: Omit<TDetailsDialogProps, "item" | "onClose">
  formDialogProps?: Omit<TFormDialogProps, "item" | "open" | "onClose">
  onView?: (item: TItem) => void
  onEdit?: (item: TItem) => void
}

export function DataTableRowActions<
  TItem extends { id: string },
  TDetailsDialogProps,
  TFormDialogProps,
>({
  item,
  onDelete,
  isLoadingDelete,
  DetailsDialogComponent,
  FormDialogComponent,
  detailsDialogProps,
  formDialogProps,
  onView,
  onEdit,
}: Readonly<
  DataTableRowActionsProps<TItem, TDetailsDialogProps, TFormDialogProps>
>) {
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false)
  const [isDetailsDialogOpen, setIsDetailsDialogOpen] = useState(false)
  const [isFormDialogOpen, setIsFormDialogOpen] = useState(false)

  const handleDelete = async () => {
    await onDelete(item.id)
    setIsDeleteDialogOpen(false)
  }

  const handleViewClick = () => {
    if (onView) {
      onView(item)
    } else if (DetailsDialogComponent) {
      setIsDetailsDialogOpen(true)
    }
  }

  const handleEditClick = () => {
    if (onEdit) {
      onEdit(item)
    } else if (FormDialogComponent) {
      setIsFormDialogOpen(true)
    }
  }

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button className="h-8 w-8 cursor-pointer p-0" variant="ghost">
            <span className="sr-only">Open menu</span>
            <MoreVerticalIcon className="h-4 w-4" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end">
          <DropdownMenuLabel>Actions</DropdownMenuLabel>
          <DropdownMenuSeparator />
          {(onView || DetailsDialogComponent) && (
            <DropdownMenuItem
              className="cursor-pointer"
              onClick={handleViewClick}
            >
              <EyeIcon className="mr-2 size-4" />
              View
            </DropdownMenuItem>
          )}
          {(onEdit || FormDialogComponent) && (
            <DropdownMenuItem
              className="cursor-pointer"
              onClick={handleEditClick}
            >
              <EditIcon className="mr-2 size-4" />
              Edit
            </DropdownMenuItem>
          )}
          <DropdownMenuSeparator />
          <DropdownMenuItem
            className="cursor-pointer text-red-600 focus:text-red-600"
            onClick={() => setIsDeleteDialogOpen(true)}
          >
            <TrashIcon className="mr-2 size-4" />
            Delete
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>

      {DetailsDialogComponent && (
        <DetailsDialogComponent
          item={isDetailsDialogOpen ? item : null}
          onClose={() => setIsDetailsDialogOpen(false)}
          {...(detailsDialogProps as TDetailsDialogProps)}
        />
      )}

      {FormDialogComponent && (
        <FormDialogComponent
          item={isFormDialogOpen ? item : null}
          onClose={() => setIsFormDialogOpen(false)}
          open={isFormDialogOpen}
          {...(formDialogProps as TFormDialogProps)}
        />
      )}

      <DeleteConfirmationDialog
        isLoading={isLoadingDelete}
        onConfirm={handleDelete}
        onOpenChange={setIsDeleteDialogOpen}
        open={isDeleteDialogOpen}
      />
    </>
  )
}

"use client"

import { X } from "lucide-react"
import type { ChangeEvent } from "react"

import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"

type DataTableSearchInputProps = {
  name: string
  setNameAction: (name: string) => void
  placeholder?: string // Adicionada a prop placeholder
}

export function DataTableSearchInput({
  name,
  setNameAction,
  placeholder = "Search...", // Usando um valor padr√£o para placeholder
}: DataTableSearchInputProps) {
  return (
    <div className="relative w-fit rounded-md md:w-64">
      <Input
        className="h-8 w-full lg:w-[250px]"
        onChange={(event: ChangeEvent<HTMLInputElement>) =>
          setNameAction(event.target.value)
        }
        placeholder={placeholder} // Usando a prop placeholder
        value={name ?? ""}
      />
      {name && (
        <Button
          className="-translate-y-1/2 absolute top-1/2 right-2 size-6 cursor-pointer rounded-md text-muted-foreground"
          onClick={() => setNameAction("")}
          size="icon"
          type="button"
          variant="ghost"
        >
          <X className="size-4" />
        </Button>
      )}
    </div>
  )
}

import type { Table } from "@tanstack/react-table"
import { Columns3CogIcon } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

type DataTableToggleColumnsProps<TData> = {
  table: Table<TData>
}

export function DataTableToggleColumns<TData>({
  table,
}: DataTableToggleColumnsProps<TData>) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          className="hidden h-8 cursor-pointer sm:flex"
          size="sm"
          variant="outline"
        >
          <Columns3CogIcon className="mr-2 h-4 w-4" />
          Columns
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-[150px]">
        <DropdownMenuLabel>Toggle columns</DropdownMenuLabel>
        <DropdownMenuSeparator />
        {table
          .getAllColumns()
          .filter(
            (column) =>
              typeof column.accessorFn !== "undefined" && column.getCanHide()
          )
          .map((column) => {
            return (
              <DropdownMenuCheckboxItem
                checked={column.getIsVisible()}
                className="cursor-pointer capitalize"
                key={column.id}
                onCheckedChange={(value) => column.toggleVisibility(!!value)}
              >
                {column.id}
              </DropdownMenuCheckboxItem>
            )
          })}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

import type { Table } from "@tanstack/react-table"
import { Columns3CogIcon, RefreshCcwIcon } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

type DataTableViewOptionsProps<TData> = {
  table: Table<TData>
}

export function DataTableViewOptions<TData>({
  table,
}: DataTableViewOptionsProps<TData>) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          className="flex h-8 w-8 cursor-pointer md:w-auto"
          size="sm"
          variant="outline"
        >
          <Columns3CogIcon className="h-4 w-4" />{" "}
          <span className="hidden md:flex">Columns</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="w-[150px]">
        <DropdownMenuLabel>Toggle columns</DropdownMenuLabel>
        <DropdownMenuSeparator />
        {table
          .getAllColumns()
          .filter(
            (column) =>
              typeof column.accessorFn !== "undefined" && column.getCanHide()
          )
          .map((column) => {
            return (
              <DropdownMenuCheckboxItem
                checked={column.getIsVisible()}
                className="cursor-pointer capitalize"
                key={column.id}
                onCheckedChange={(value) => column.toggleVisibility(!!value)}
              >
                {column.id}
              </DropdownMenuCheckboxItem>
            )
          })}
        <DropdownMenuSeparator />
        <DropdownMenuItem
          onClick={() => {
            table.resetColumnVisibility()
          }}
