"use client"

import Link from "next/link"
import { usePathname } from "next/navigation"

import type { NavItemType } from "@/components/layout/navbar/types"
import { Button } from "@/components/ui/button"
import {
  NavigationMenu,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  navigationMenuTriggerStyle,
} from "@/components/ui/navigation-menu"

import { cn } from "@/lib/utils"

function NavItem({ label = "label", href = "#" }: NavItemType) {
  const pathname = usePathname()

  return (
    <NavigationMenuItem>
      <NavigationMenuLink
        asChild
        className={navigationMenuTriggerStyle({
          className: cn(
            "h-8 bg-transparent px-3 text-base text-muted-foreground hover:bg-transparent hover:text-foreground focus:bg-transparent focus:text-foreground",
            pathname === href ? "text-primary" : null
          ),
        })}
      >
        <Link href={href}>{label}</Link>
      </NavigationMenuLink>
    </NavigationMenuItem>
  )
}

type NavMainAuthProps = {
  position?: string
  items: NavItemType[]
  className?: string
}

export function NavMainAuth({ position, items, className }: NavMainAuthProps) {
  if (items.length === null) {
    return null
  }

  return (
    <div
      className={cn(
        "hidden w-full items-center justify-start md:flex",
        {
          "justify-start": position === "start" || position === "left",
          "justify-center": position === "center",
          "justify-end": position === "end" || position === "right",
        },
        className
      )}
    >
      <NavigationMenu viewport={false}>
        <NavigationMenuList>
          {items.map((item) => (
            <NavItem href={item.href} key={item.href} label={item.label} />
          ))}
          <Button asChild size="sm">
            <Link href={"/auth/sign-in"}>Sign in</Link>
          </Button>
          <Button asChild size="sm" variant="outline">
            <Link href={"/auth/sign-up"}>Sign up</Link>
          </Button>
        </NavigationMenuList>
      </NavigationMenu>
    </div>
  )
}

import { Separator } from "@/components/ui/separator"
import { SidebarTrigger } from "@/components/ui/sidebar"

import { cn } from "@/lib/utils"

import { ToggleTheme } from "@/registry/components/shared/toggle/toggle-theme"

export function NavSystem({ className }: { className?: string }) {
  return (
    <div className={cn("flex h-5 items-center gap-1", className)}>
      <ToggleTheme className="flex" />
      <Separator className="flex md:hidden" orientation="vertical" />
      <SidebarTrigger className="flex size-8 cursor-pointer text-muted-foreground md:hidden" />
    </div>
  )
}

import Link from "next/link"

import { NavSystem } from "@/components/layout/navbar/inc/nav-system"
import type { NavbarAuth01Type } from "@/components/layout/navbar/types"

import { cn } from "@/lib/utils"

import { NavMainAuth } from "@/registry/components/layout/navbar/inc/nav-main-auth"
import { Container } from "@/registry/components/shared/container"

export type NavbarAuth01Props = {
  navbar: NavbarAuth01Type
}

export function NavbarAuth01({ navbar }: NavbarAuth01Props) {
  const shouldApplyFullWidth = !(
    navbar.navItems ||
    navbar.logo ||
    navbar.logoIcon
  )

  return (
    <div className="fixed top-0 z-50 flex h-16 w-full shrink-0 items-center border-border/60 border-b backdrop-blur supports-[backdrop-filter]:bg-background/90">
      <Container className="flex items-center justify-center" size="2xl">
        <div className="relative flex w-full items-center justify-between gap-2">
          <Link
            className="mr-4 flex items-center rounded-md outline-none transition-all focus-visible:border-ring focus-visible:ring-[3px] focus-visible:ring-ring/50"
            href="/"
          >
            <div className="inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md font-medium text-sm outline-none transition-all">
              <div className="flex shrink-0 items-center gap-2">
                {navbar.logoIcon && (
                  <div className="flex aspect-square items-center justify-center rounded-md bg-sidebar-primary p-1 text-sidebar-primary-foreground">
                    {navbar.logoIcon}
                  </div>
                )}

                {navbar.logo && (
                  <span
                    className={cn(
                      "shrink-0 font-bold text-3xl leading-normal",
                      navbar.logoResponsive &&
                        navbar.logoIcon &&
                        "hidden md:flex"
                    )}
                  >
                    {navbar.logo}
                  </span>
                )}
              </div>
            </div>
          </Link>

          {navbar.navItems && (
            <NavMainAuth items={navbar.navItems} position={navbar.position} />
          )}

          <NavSystem
            className={shouldApplyFullWidth ? "w-full justify-end" : undefined}
          />
        </div>
      </Container>
    </div>
  )
}
